INSERT INTO `tb_test_item` VALUES ('698', '<p><strong>Questions 181 – 185 refer to the following invitation and e-mail</strong></p>\r\n<p><strong> </strong></p>\r\n<div>\r\n<p>Linens &amp; Things Home Furnishings Warehouse</p>\r\n<p>Annual Christmas Party</p>\r\n<p>Friday, December 15</p>\r\n<p>Club Roma</p>\r\n<p> </p>\r\n<p>6:30 – 7:30 P.M</p>\r\n<p>“Mix and Mingle”</p>\r\n<p>A cash bar will open at 6:30 to serve beverages.</p>\r\n<p> </p>\r\n<p>7:30 – 8:30 P.M</p>\r\n<p>Dinner</p>\r\n<p>A Buffet Dinner of Pasta, Vegetables, Meat, and Seafood</p>\r\n<p> </p>\r\n<p>8:30 – 9:00 P.M</p>\r\n<p>End of the Year Speech and Employee Recognition</p>\r\n<p>Martha Williams, CEO</p>\r\n<p>9:00 – 9:30 P.M</p>\r\n<p>A comedy performance by the human resources Department</p>\r\n<p>“A Day at the Office”</p>\r\n<p> </p>\r\n<p>9:30 P.M – 12:00 midnight</p>\r\n<p>Music and Dancing</p>\r\n<p>With The Stevie Franks Jazz Trio</p>\r\n<p> </p>\r\n</div>\r\n<p> </p>\r\n<p>                </p>\r\n<div>\r\n<p> </p>\r\n<p>Attention: Members of the Human Resources Department</p>\r\n<p>RE: Christmas Party Performance</p>\r\n<p> </p>\r\n<p>I would like to remind all members of the Human Resources Department that we will be starting to rehearse our comedy performance after work from 6:30 to 8:00 p.m. every Thursday for the next three weeks. We only have three weeks left until the Christmas party!</p>\r\n<p> </p>\r\n<p>All staff members MUST attend these rehearsals, please e-mail myself, Richard Cross, at rcross@linensnthings prior to Thursday evening. Also, our final rehearsal is the night before our Christmas dinner. Please bring your costumes that night.</p>\r\n<p> </p>\r\n<p>See you Thursday,</p>\r\n<p>Richard Cross</p>\r\n<p>Director, Human Resources</p>\r\n</div>', '9', '4', '2', 'In a ballroom', 'In a club', 'In a dance studio', 'In the HR Department', '<p><strong>Where will the party be held?</strong></p>', '181', '0', '1', '1', 'vi', '0', '2015-11-11 14:57:28', '0000-00-00 00:00:00');
