INSERT INTO `tb_test_item` VALUES ('1008', '', '8', '5', '1', 'Overwhelming', 'Debatable', 'Predictable', 'Reduced', '<p><strong>the number of submissions has been____. This is the first time the position has become </strong></p>\r\n<p><strong>available in more than 10 years, and many qualified professionals are eager to fill the shoes of the pervious assistant director. Due to the large quality of applications, we do not expect to finish assessing each candidate’s qualifications until the end of February. After this process is complete, someone from our office will contact you about your application. Thank you for your patient in this matter, and we look forward to speaking with you soon if your application is among those selected for future review.</strong></p>\r\n<p><strong>Sincerely,</strong></p>\r\n<p><strong>Jose Melendez, Human resources</strong></p>\r\n<p><strong> </strong></p>', '143', '0', '1', '1', 'vi', '0', '2015-11-15 20:33:56', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('1009', '<p><strong>Question 144-146 refer to the following email</strong></p>', '8', '5', '2', ' receipt', 'recipient', ' receptor', 'reception', '<p><strong>Ms. Lisa Debonaire</strong></p>\r\n<p><strong>12 State St.</strong></p>\r\n<p><strong>Chicago, IL 60112</strong></p>\r\n<p><strong>Dear Mr. Debonaire,</strong></p>\r\n<p><strong>I would like to personally congratulate you on your recent accomplishments as a filmmaker. You have been selected by our board as the next____of the Independent Film Award.</strong></p>\r\n<p><strong>This is a great honor that is awarded to only one filmmaker per year; previous winner of the award include Academy Award- winning directors. </strong></p>', '144', '0', '1', '1', 'vi', '0', '2015-11-15 21:42:13', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('1010', '', '8', '5', '3', 'Produce', 'Producer', 'Produces', 'Producing', '<p><strong>Our board chooses award- winners from a select group of filmmakers who have unusual talent and bring something fresh and inspiring to the film indunstry. When selecting a winner, we consider the overall quality of the films that a filmmaker_____, as well as the direction, cinematography, and relevance to society.</strong></p>', '145', '0', '1', '1', 'vi', '0', '2015-11-15 21:44:03', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('1011', '', '8', '5', '4', 'some', ' much', 'most', 'such', '<p><strong>Filmmakers who produce their work on lower budgets receive special consideration from our board. We are aware of your limited budget, and were impressed that you managed to create____ a high-quality film given the lack of financial backing.</strong></p>\r\n<p><strong>After watching hundreds of films, we chose you as our winner because of your incredible film entitled “The Wanderer”. It is a particularly a striking piece of work because of its poignant themes.</strong></p>\r\n<p><strong>In order to honor your accomplishments, we would like to invite you to an Independent Films Award banquet. The event will take place on February 15 at 7:00 p.m. at the Hyatt Hotel ballroom in the New York City.</strong></p>\r\n<p><strong>Sincerely,</strong></p>\r\n<p><strong>Michael Higgins</strong></p>\r\n<p><strong>Independent Films, Inc. </strong></p>', '146', '0', '1', '1', 'vi', '0', '2015-11-15 21:45:07', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('1012', '<p><strong>Question 147-149 refer to the following letter</strong></p>', '8', '5', '2', 'Even', 'If', 'Then', 'Instead', '<p><strong>Susan Queller</strong></p>\r\n<p><strong>530 Morning Rd.</strong></p>\r\n<p><strong>Chicago, IL 60019</strong></p>\r\n<p><strong>Dear Ms. Queller,</strong></p>\r\n<p><strong>You may already aware that our company’s theatre productions will no longer be held in the Downtown Arts Theatre. Although this theatre provided a venue for our performances for many years, it is time for a change. We hope that as a loyal member, you will continue to join us for performances. The new theatre was approved in a majority vote by our board members this past season.</strong></p>\r\n<p><strong>____you would like to review your membership now for the 2009-2010 season, you will be</strong></p>\r\n<p><strong>among the first to choose seating in our new Alfred A. Montrose Theatre. </strong></p>', '147', '0', '1', '1', 'vi', '0', '2015-11-15 21:48:53', '0000-00-00 00:00:00');
