INSERT INTO `tb_test_item` VALUES ('1319', '<p><strong>Question 169-171</strong></p>\r\n<p style=\"text-align: center;\"><strong>To All Renforth Ltd. Employees Using the Fitness Center</strong></p>\r\n<p><strong>As of September 1, corporate membership fees will change. Although this does mean a slight rise in the overall cost of your gym membership, please bear in mind that Renforth Ltd. Employees and their families still receive a substantial discount.</strong></p>\r\n<p><strong>The Renforth Fitness Center has individual and family pricing for weekends only, weekdays only, or for seven days a week. The adjusted annual rates are provided below.</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td rowspan=\"2\" valign=\"top\" width=\"385\">\r\n<p align=\"center\"><strong>The Renforth Fitness Center</strong></p>\r\n<p align=\"center\"><strong>Price Guide</strong></p>\r\n</td>\r\n<td colspan=\"2\" valign=\"top\" width=\"234\">\r\n<p align=\"center\"><strong>Renforth Corporate Membership</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"113\">\r\n<p><strong>Individual</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"121\">\r\n<p><strong>Family</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"385\">\r\n<p><strong>weekends only (Mon ~ Fri. 5 a.m ~ 11 p.m)</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"113\">\r\n<p><strong>$200</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"121\">\r\n<p><strong>$240</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"385\">\r\n<p><strong>weekdays only (Sat ~ Sun. 5 a.m ~ 6 p.m)</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"113\">\r\n<p><strong>$125</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"121\">\r\n<p><strong>$200</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"385\">\r\n<p><strong>Full Membership (weekends 5 a.m ~ 8 p.m. and weekdays 5 a.m ~ 5 p.m)</strong></p>\r\n</td>\r\n<td valign=\"to