INSERT INTO `tb_test_item` VALUES ('1322', '<p>Question 172-175</p>\r\n<p align=\"center\"><strong>Job Opening: Plant Supervisor for Leading Biochemical Company</strong></p>\r\n<p><strong>The nation’s largest biochemical company, Triston Biochem, is currently seeking a new supervisor to work at a production facility in Houston, Texas. Relocation to Houston will be necessary in order to fulfill job requirements. The Plant Supervisor will be responsible for ensuring safety standards at the facility and communicating with company representatives in Oklahoma and Delaware. A university degree is required; former experience in the biochemical field is an asset. A prepared to offer a competitive salary and a housing allowance.</strong></p>\r\n<p><strong>Please forward a resume and cover letter, along with a salary history by November 18 to: </strong></p>\r\n<p><strong>Julia Remarque, Human Resources Manager</strong></p>\r\n<p><strong>Triston Biochem</strong></p>\r\n<p><strong>44 Ridgemont Drive, Houston, TX</strong></p>\r\n<p><strong>No phone calls, please.</strong></p>', '9', '8', '4', 'Delaware', 'Oklahoma', 'Toronto', 'Houston', '<p><strong>Where is the job located?</strong></p>', '172', '0', '1', '1', 'vi', '1', '2015-11-21 19:41:03', '2015-11-21 19:41:18');
