INSERT INTO `tb_test_item` VALUES ('1756', '<p>Question 166- 169</p>\r\n<p> </p>\r\n<p>REGIONAL ORGANIZATION of NURSE PRACTITIONERS</p>\r\n<p>TWENTY FIFTH ANNUAL CONFERENCE REGISTRATION INFORMATION</p>\r\n<p> </p>\r\n<p>Conference Dates: Friday, September 2- Sunday, September 4</p>\r\n<p>Conference fee: RONP Members: $43/ Non- members: $65</p>\r\n<p>Please mail the attached registration form and your personal check to:</p>\r\n<p>Attn. Leanne Segway, Human Relation Officer</p>\r\n<p>54 Newmarket Drive</p>\r\n<p>Barnaby, WV</p>\r\n<p>789078</p>\r\n<p>- Only personal checks will be accepted via mail. If you would prefer to pay for your registration costs by credit card, please contact our event planner, Gillian Stevens, at 787- 545- 7566</p>\r\n<p>- Registration forms and complete payment must be submitted by Monday, August 19. Once registration and payment have been received, we will mail you a confirmation letter and conference package.</p>\r\n<p>- The conference workshops will take place at the Grandview Hotel. Participant are entitled to complimentary breakfast. Lunch will also provided. Participants are to make their own arrangements for dinner</p>\r\n<p>- Rooms have been reserved at the Grandview Hotel and are being offered at discounted rates for RONP members. If you would like to seek accommodation elsewhere, contact Gillian Stevens at 787- 545-7566. Gillian will be happy to assit you in finding a local hotel.</p>\r\n<p>- Should you be unable to attend the conference, please call Fiona Thompson or Robert Smith at our conference headquarters- 897- 443- 3858. All cancellations made by August 31 will be refund in full.</p>', '9', '11', '1', 'Leanne Segway', 'Robert Smith', 'Fiona Thompson', 'Gillian Stevens', '<p><strong>To whom should the registration fee be mailed?</strong></p>', '166', '0', '1', '1', 'vi', '0', '2015-11-27 16:52:20', '0000-00-00 00:00:00');
