INSERT INTO `tb_test_item` VALUES ('1959', '<p><strong>Question 186-190</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p align=\"right\"><strong>The Fidelity Bank</strong></p>\r\n<p align=\"right\"><strong>5665 Peachtree Dunwoody Road</strong></p>\r\n<p align=\"right\"><strong>Atlanta, Georgia 30348</strong></p>\r\n<p align=\"left\"><strong>Mr. Johnny Sermon</strong></p>\r\n<p align=\"left\"><strong>Office Furnishing Co.                                                                                                                                                         June 7</strong></p>\r\n<p align=\"left\"><strong>Dear Mr. Sermon,</strong></p>\r\n<p align=\"left\"><strong><span style=\"text-decoration: underline;\">Order 1479</span></strong></p>\r\n<p align=\"left\"><strong>I’m writing in connection with your email connecting the above order for office furniture. Unfortunately, we have not yet received the filing cabinets which were a part of the order.</strong></p>\r\n<p align=\"left\"><strong>It will be appreciated if you deliver those cabinets as soon as possible of refund our money.</strong></p>\r\n<p align=\"left\"><strong>We look forward to hearing from you soon. Since the error has caused too much trouble, we have no choice but to take legal action if we are unable to get the goods within seven days.</strong></p>\r\n<p align=\"left\"><strong>Sincerely,</strong></p>\r\n<p align=\"left\"><strong>Ms. Jonas Magee</strong></p>\r\n<p align=\"left\"><strong>Office Manager</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p align=\"center\"><strong><span style=\"font-size: medium;\">Office Furnishing Co.</span></strong></p>\r\n<p><strong>1001 Johnson Ferry Road NE</strong></p>\r\n<p><strong>Atlanta, GA 30342-1600</strong></p>\r\n<p><strong>Ms Jonas Magee</strong></p>\r\n<p><strong>Office Manager</strong></p>\r\n<p><strong>The Fidelity Bank                                                                                                                                                             June 10</strong></p>\r\n<p><strong>Dear Jonas,</strong></p>\r\n<p><strong>Thank you for your letter enquiring about the filing cabinets. We apologize for the delay in delivering the cabinets. As I said in my email, there has been a problem at our suppier’s factory. Because this kind of problem is completely out of our control, we are not responsible for the delay, and, therefore, it is impossible to refund the money. Please refer to our Terms of Sale I enclosed with this letter.</strong></p>\r\n<p><strong>As we expect to receive the cabinets within a couple of days, you will get them soon.</strong></p>\r\n<p><strong>Your sincerely,</strong></p>\r\n<p><strong>Johnny Sermon</strong></p>\r\n<p><strong>Sales Executive</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>', '9', '10', '4', 'To complain a damage of shipment', 'To place an order', 'To respond to an email', 'To complain the late delivery of shipment ', '<p><strong>Why the first letter written?</strong></p>', '186', '0', '1', '1', 'vi', '0', '2015-12-02 09:55:29', '0000-00-00 00:00:00');
