INSERT INTO `tb_test_item` VALUES ('2231', '<p><strong>Question 181-185</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p align=\"center\"><span style=\"font-size: medium;\"><strong>Welcome to The Georgian Terrace Hotel</strong></span></p>\r\n<p align=\"left\"><strong>Since 1912, The Georgian Terrace Hotel has been the venue of choice for Atlanta’s most prominent events, including hosting the world premiere reception for “Gone With the Wind” in 1939.</strong></p>\r\n<p align=\"left\"><strong>Listed on the National Register of Historic Places, the hotel has linked its storied past with and 307 spacious and elegant suites along with state-of-the-art conference facilities.</strong></p>\r\n<p align=\"left\"><strong>The hotel offers the latest technology in 16,000 square feet of elegant function space, as well as bilingual world-class staff to assist with both international business meeting and special social events.</strong></p>\r\n<p align=\"left\"><strong>The Georgian Terrace Hotel. Historic, yet ahead of its time.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><strong>Georgian Terrace Historic Renovation</strong></p>\r\n<p><strong>Atlanta, Georgia</strong></p>\r\n<p><strong>The renovation of this historic Atlanta landmark hotel, built in 1912, included the creation of 8 units within the original structure, the design of a new 19-story, 214-unit tower, 53,000 SF of retail and entertainment space, a renovation of the original public spaces and banquet facilities, a roof top swimming pool and a nine-level, 700-space parking lot.</strong></p>\r\n<p><strong>The project received an award from the Atlanta Urban Design Commission, in 1992.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '13', '1', 'A reception for a famous movie', 'A summit meeting', 'A music concert', 'An international conference', '<p><strong>What kind of event was held in the Georgian Terrace Hotel?</strong></p>', '181', '0', '1', '1', 'vi', '0', '2015-12-08 12:29:25', '0000-00-00 00:00:00');
