INSERT INTO `tb_test_item` VALUES ('2473', '<p><strong>Question 158-159</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><span style=\"font-size: medium;\"><strong>Braciole</strong></span></p>\r\n<p><strong>3 cups tomato sauce</strong></p>\r\n<p><strong>1 ¼ cups flavored croutons</strong></p>\r\n<p><strong>1/3 cup grated Parmesan</strong></p>\r\n<p><strong>2 eggs</strong></p>\r\n<p><strong>1 tablespoon chopped fresh parsley</strong></p>\r\n<p><strong>1 tablespoon chopped fresh oregano</strong></p>\r\n<p><strong>1 teaspoon finely chopped rosemary</strong></p>\r\n<p><strong>1 teaspoon finely chopped thyme</strong></p>\r\n<p><strong>1 clove garlic</strong></p>\r\n<p><strong>1 pound flank steak, pounded to ¼-inch thick</strong></p>\r\n<p><strong>Olive oil, for brushing</strong></p>\r\n<p><strong>Salt and pepper</strong></p>\r\n<p><strong>Vegetable oil, for searing</strong></p>\r\n<p><strong>Preheat the oven to 350 degrees F. Place the tomato sauce in a 9 by 13-inch baking dish and place in the oven to heat.</strong></p>\r\n<p><strong>In a food processor bowl mix the croutons, cheese, eggs, herbs and garlic until it forms a paste.</strong></p>\r\n<p><strong>Brush the pounded flank steak with the olive oil and season generously with salt and pepper.</strong></p>\r\n<p><strong>Spread the filling evenly over the meat. Roll tightly and tie with butcher’s twine.</strong></p>\r\n<p><strong>In a large saute pan heat 1 to 2 tablespoons of vegetable oil and sear all sides of the rolled meat. Braise for 35 minutes or, up to 3 hours.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>', '9', '16', '3', '¼', '2', '3', '4 ', '<p><strong>How many cups of tomato sauce should be used, according to this recipe?</strong></p>', '158', '0', '1', '1', 'vi', '0', '2015-12-12 14:33:19', '0000-00-00 00:00:00');
