INSERT INTO `tb_test_item` VALUES ('2475', '<p><strong>Question 160-162</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><span style=\"font-size: small;\"><strong>Internet marketing tips you can’t afford to miss.</strong></span></p>\r\n<p><strong>E-Marketing News brings you the inside scoop on the search engines industry, including exclusive interviews with top search engine insiders.</strong></p>\r\n<p><strong>Downloaded more than 9,000 times – get your copy of ‘Filthy Linking Rich’</strong></p>\r\n<p><strong>In depth interviews include:</strong></p>\r\n<p><strong>* Feature length audio interview with Google’s Matt Cutts. Part 1 – Part 2.</strong></p>\r\n<p><strong>* Jim Lanzone &amp; Apostolos Gerasoulis, Ask Jeeves. Google doesn’t use PageRank.</strong></p>\r\n<p><strong>* Yahoo Senior Manager Search Jon Glick covers the new Yahoo in a very in-depth conversation.</strong></p>\r\n<p><strong>*Google engineer Daniel Dulitiz explains about the toolbar, crawling, linking, cloaking, spamming and more.</strong></p>\r\n<p><strong>* Inside the Teoma Search Algorithm, an interview with Paul Gardi, Teoma’s Senior Vice President of Search.</strong></p>\r\n<p><strong>* Google’s Senior Research Scientist, Craig Nevill-Manning talks about the Florida Update.</strong></p>\r\n<p><strong>E-Marketing News is a free newletter edited by Mike Grehan, author of Search Engine Marketing: The essential best practice guide and Christine Churchill, President of KeyRedevance.</strong></p>\r\n<p><strong>Each month Mike, Christine, anf the Search Marketing Dream Team bring you the latest news covering every aspect of Internet marketing, a-commerce solutions and web site development  and design. Make sure you’re on the mailing list for the newsletter online marketing professionals prefer = it’s wry, not dry.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>', '9', '16', '2', 'The automotive industry', 'The search engines industry', 'The marketing industry', 'The electrical engineering industry', '<p><strong>What does E-Marketing News bring you the inside scoop on?</strong></p>', '160', '0', '1', '1', 'vi', '0', '2015-12-12 14:35:06', '0000-00-00 00:00:00');
