INSERT INTO `tb_test_item` VALUES ('2481', '<p><strong>Question 166-168</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><strong>THEME PARK TICKET DETAILS</strong></p>\r\n<p><strong>2-PARK UNLIMITED ADMISSIONN</strong></p>\r\n<p><strong>Unlimited park-to-park admission to both Universal Studios Florida AND Universal Islands of Adventure theme parks for up to seven (7) consecutive days.</strong></p>\r\n<p><strong>Free aadmission to select live entertainment venes* at Universal CityWalk entertainment complex.</strong></p>\r\n<p><strong>This offer is only available online and is not available at the front gate of either theme park.</strong></p>\r\n<p><strong>PLEASE NOTE THE FOLLOWING:</strong></p>\r\n<p><strong>* Tickets expire six (6) days after first day of use at either theme park or CityWalk.</strong></p>\r\n<p><strong>* Photo ID must be presented at the front gate turnstile and CityWalk.</strong></p>\r\n<p><strong>* Signature may be required on tickets.</strong></p>\r\n<p><strong>* Limit of six (6) tickets per person</strong></p>\r\n<p><strong>* Parking not included</strong></p>\r\n<p><strong>* Tickets do not include admission to Wet n’ Wild.</strong></p>\r\n<p><strong>* Not valid for separately ticketed concerts and special events. Some venues require 21 or older for admission. Proof of ID required. Additional restrictions may apply. Prices do not include tax and are subject to change without notice.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '16', '4', 'Universal Studios theme park', 'Islands of Adventure theme park ', 'Wet n’ Wild', 'CityWalk', '<p><strong>Where do you receive free admission to select live entertainment venues?</strong></p>', '166', '0', '1', '1', 'vi', '0', '2015-12-12 16:09:10', '0000-00-00 00:00:00');
