INSERT INTO `tb_test_item` VALUES ('2570', '<p>Question 153- 154</p>\r\n<p> </p>\r\n<p><strong>AD Watcher</strong></p>\r\n<p><strong>AD Watcher </strong>is our new e-mail alert service. Hundreds of new listings are added daily to the Bargain News. With AD Watcher, you will be informed daily when new listings that match your search criteria arrive.</p>\r\n<p>Best of All.... It &rsquo; s easy! Simply perform your search and click on AD watcher. You will be notified daily of new listings and weekly status updates every Thursday.</p>\r\n<p>Ad Watcher is an advanced feature available to all registered Bargain News users.</p>', '9', '15', '2', 'A home security system', 'An e-mail alert service', 'A diet and exercise program', 'An enviromental protection group', '<p>What is Ad Watcher?</p>', '153', '0', '1', '1', 'vi', '0', '2015-12-14 15:43:03', '0000-00-00 00:00:00');
