INSERT INTO `tb_test_item` VALUES ('2913', '<p><strong>Question 186-190</strong></p>\r\n<table style=\"width: 714px;\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"714\">\r\n<p><strong>Ajax Machine Tools Ltd. Service Contract</strong></p>\r\n<p><strong>Contract No: 2008-0206012                               Customer Account No.: 03487</strong></p>\r\n<p><strong>Customer Name: Tay Young Manufacturing Co. </strong></p>\r\n<p><strong>Contract Date: February 6                                Service Commencement Date: February 6</strong></p>\r\n<p><strong>Contract Term: </strong></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"349\">\r\n<ul>\r\n<li><strong>2 years </strong></li>\r\n</ul>\r\n</td>\r\n<td valign=\"top\" width=\"349\">\r\n<p>ü <strong>3 years</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong>Equipment: Industrial Puncher                          Model: Renz Super 700</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Under this contract, the Industrial Puncher Renz Super 700 is under warranty to be free of manufacturing defects within the time period of coverage stated above.</strong></p>\r\n<p><strong>Ajax Machine Tools will provide routine maintenance checks twice a year on site by a trained technician, whose services will include cleaning and a thorough systems check.</strong></p>\r\n<p><strong>When the inspection discloses defect or malfunction, the technician will issue a repair recommendation. The purchaser must submit a signed copy of the technician’s recommendation to the contractor for replacement of defective parts. Ajax Machine Tools Ltd. shall not assume liability or responsibility for damages due to improper handling, installation or maintenance by the customer, as they are not considered manufacturing defects. Thus, such damages are not covered by this contract.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><strong>Mira Kang</strong></p>\r\n<p><strong>Tay Young Manufacturing Co.</strong></p>\r\n<p><strong>34 Rathburn Road</strong></p>\r\n<p><strong>Lincoln, NE 68507</strong></p>\r\n<p><strong>August 31</strong></p>\r\n<p><strong>Kevin Kensington</strong></p>\r\n<p><strong>Ajax Machine Tools Ltd.</strong></p>\r\n<p><strong>725 Sheppard Avenue</strong></p>\r\n<p><strong>Denver, CO 80211</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Dear Mr. Kensington</strong></p>\r\n<p><strong>My company purchased the industrial puncher (Renz Super 700) from your company in February. However, we faced some problems while using the machine over the past 6 months. We pointed this our to your technician during the regular maintenane check on July 15, and during the inspection he found some parts that needed to be replaced. He said the he would forward his service report with a repair recommendation to the relevant department so that the defective parts could be replaced. However, we have not heard from him since then.</strong></p>\r\n<p><strong>We understand that there are no more free maintenance checks temaining for this year, but we would like to have our machine repaired nonetheles. Please let us know when you think you will be able to send the technician once again and supply the replaced parts for our machine.</strong></p>\r\n<p><strong>Sincerely,</strong></p>\r\n<p><strong>Mira Kang</strong></p>\r\n<p><strong>Tay Young Manufacturing Co.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>', '9', '18', '3', 'For one year ', 'For two  years ', 'For three years ', 'For four years ', '<p><strong>How long is the contract valid?</strong></p>', '186', '0', '1', '1', 'vi', '0', '2015-12-21 10:24:51', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('2914', '<p><strong>Question 155-156</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"619\">\r\n<p><strong>We’re pleased to announce that all employees of BMC Corporation now have access to our online document archieve. Our IT team has worked for over four months to provide this convenient and efficient system. This will enable employees to gain access to the archive anytime and anywhere, whether they are working in the office, on the road or at home. Follow the directions below to take advantage of the online document archive.</strong></p>\r\n<ol>\r\n<li><strong>Go to the doument section of the website <a href=\"http://www.bmc.com/docs\">www.bmc.com/docs</a></strong></li>\r\n<li><strong>Click on “archive” to search the database</strong></li>\r\n<li><strong>To use the archive you need to log in:</strong><ol>\r\n<li><strong>Enter user name (employee’s e-meil address)</strong></li>\r\n<li><strong>Enter password (employee identification card number)</strong></li>\r\n</ol></li>\r\n<li><strong>Use the database research to find the document you are looking for.</strong></li>\r\n</ol></td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '18', '3', 'College students', 'System analysts', 'BMC employees', 'BMC shareholders', '<p><strong>Who is this announcement for?</strong></p>\r\n<p><strong> </strong></p>', '155', '0', '1', '1', 'vi', '0', '2015-12-21 10:26:20', '0000-00-00 00:00:00');
