INSERT INTO `tb_test_item` VALUES ('3319', '<p><strong>Question 191-195</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Ms. Parker,</strong></p>\r\n<p><strong>We would like to thank you for choosing us to book your tour of South Korea. The temporary schedule is listed below and includes the hotel reservation.</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Day</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Departure</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Time</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Arrival</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Time</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Sunday</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Hong Kong</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>2:00 p.m.</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Seoul</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>6:50 p.m.</strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Tuesday</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Seoul</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>12:00 noon</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>Hong Kong</strong></p>\r\n</td>\r\n<td valign=\"top\" width=\"118\">\r\n<p><strong>4:45 p.m.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<p><strong>We regret to inform you that because you only signed up 7 passengers, we cannot provide you a group discount rate. Our policy is that a group requires a minimum of 10 people in order to qualify for 15% discount on airfare.</strong></p>\r\n<p><strong>Your payment options are credit card, money order or direct online transfer.</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Harold Klum</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p><strong> </strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Mr. Klum,</strong></p>\r\n<p><strong>Thank you for the speedy reply. We are really looking forward to our tour. We would like to leave on January 15<sup>th</sup>. We would also like to get a more detailed itinerary for our time in Seoul.</strong></p>\r\n<p><strong>As for the tour, we have two more people who signed up for the trip, and despite our best efforts we can’t seem to find one more person. But we feel that with nine people we should qualify for the discount, or a discount of some kind. I realize you have a company policy, but we thought you could bend it just this time. We hope to have a long relationship with you in the future.</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Thanks again,</strong></p>\r\n<p><strong>Ellie Parker</strong></p>\r\n<p><strong> </strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p> </p>', '9', '22', '2', 'To confirm the hotel reservation', 'To inform Ms. Parker that she got a discount', 'To provide travel information', 'To provide flight times', '<p><strong>What is NOT the purpose of the first e-mail?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '191', '0', '1', '1', 'vi', '0', '2015-12-27 01:40:58', '0000-00-00 00:00:00');
