INSERT INTO `tb_test_item` VALUES ('3362', '', '8', '20', '1', 'collection', 'collective', 'collect', 'collector', '<p>The 50 cans of paint that we received in error will be returned to your order.......... department this afternoon by our staff. The paint that we received was Blizzard White ( product #124), while what we requested was Blazing White (produc #421). Please refer to the attached invoice for additional information.</p>\r\n<p>The prices of the paint are the same, so we should be able to do a direct exchange with no additional charges. Because we will need to pay for shipping when we return the paint, we ask that you credit our account for these charges.</p>', '142', '0', '1', '1', 'vi', '0', '2015-12-28 15:08:29', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3363', '', '8', '20', '4', 'propose', 'conclude', 'hear', 'confirm', '<p>We would like for you to.......... that you have received this fax by sending us back a fax at 902-212-2134 or calling me directly at 902-212-2133. Please ship the correct items to us as soon as possible.</p>\r\n<p>Thank you for your attention to this matter.</p>\r\n<p>Sincerely,</p>\r\n<p>Charles Johnson</p>\r\n<p>Manager</p>', '143', '0', '1', '1', 'vi', '0', '2015-12-28 15:11:42', '0000-00-00 00:00:00');
