INSERT INTO `tb_test_item` VALUES ('3370', '<p>Question 147- 149</p>\r\n<p> </p>\r\n<p> </p>', '8', '20', '1', 'about', 'while', 'since', 'as', '<p>Mr. Darrel Hannet</p>\r\n<p>315 Central St.</p>\r\n<p>Baltimore, MD 21220</p>\r\n<p>Dear Mr. Hannet</p>\r\n<p>Our records indicate that you contacted us on 9/1/07......... a malfunctioning LCD on your camera. Our customer service desk has forwarded your concerns to us so that we can help you further. </p>', '147', '0', '1', '1', 'vi', '0', '2015-12-28 15:28:12', '0000-00-00 00:00:00');
