INSERT INTO `tb_test_item` VALUES ('3372', '', '8', '20', '2', 'recruiting', 'repair', 'advertising', 'security', '<p>You also stated that you cannot preview images before taking photos. In order to serve you better, we suggest that you meet with a service representative. This type of problem is not easily resolved over the telephone, please bring your camera into the Nokis.......... center in Baltimore.</p>\r\n<p>There you will receive further assistance in diagnosing the problem. If you need to contact us, our customer service number is 1- 800- 321- 1234. Let us know if we can be of any further assistance.</p>\r\n<p>Sincerely,</p>\r\n<p>Charles Satara</p>', '149', '0', '1', '1', 'vi', '0', '2015-12-28 15:33:15', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3373', '<p>Question 150 -152</p>', '8', '20', '4', 'appointment', 'engagement', 'commitment', 'involvement', '<p>To: <a href=\"mailto:Jsong@medstat.org\">Jsong@medstat.org</a></p>\r\n<p>From: <a href=\"mailto:director1@medconsult.org\">director1@medconsult.org</a></p>\r\n<p>Date: Oct.,8,2008</p>\r\n<p>Subject: Workshops</p>\r\n<p>Dear Mr. Song,</p>\r\n<p>Thank you for your recent participation in the MedConsult Professional Organization &rsquo; s introductory workshop. We hope that you found your......... in the session to be helpful. </p>', '150', '0', '1', '1', 'vi', '0', '2015-12-28 15:36:39', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3374', '', '8', '20', '1', 'for instance', 'however', 'in lieu of this', 'in contrast', '<p>This introductory workshop is only one of our many offerings. This year we will be continuing our outreach to local medical professionals,.........., a new series of development workshops will be provided on a bi-weekly basis.</p>', '151', '0', '1', '1', 'vi', '0', '2015-12-28 15:39:08', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3375', '', '8', '20', '4', 'offer', 'to offer', 'offering', 'offered', '<p>These sessions are only.......... to those who become registered members by signing up at medconsultworkshops.com/register.</p>\r\n<p>We hope that you will choose to register soon and continue your professional development.</p>\r\n<p>We also hope that you will be able to participate in this year &rsquo; s new series of workshops. Should you have any questions, please do not hesitate to contact us.</p>\r\n<p>Sincerely,</p>\r\n<p>John Healy, Director</p>', '152', '0', '1', '1', 'vi', '0', '2015-12-28 15:59:12', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3376', '<p><strong>Question 169-172</strong></p>\r\n<p><strong>Telecommunications Merger Announced</strong></p>\r\n<p><strong>Los Angeles, February 2</strong></p>\r\n<p><strong>Two of the country &rsquo; s biggest telecommunications companies have signed a deal, completing the largest merger in history. The news was announced at a press conference this morning. Delmore, Inc. and Lasing, Corp. will come together in March to create Del-Lasing. The new corporation is expected to have combined assets of over $1 billion.</strong></p>\r\n<p><strong>By joining Delmore &rsquo; s technological innovation and Lasing &rsquo; s marketing strategies, the company hopes to reach even more customers both in the U.S. and overseas with their cellular phone and Internet services. Del-Lasing will be based in San Francisco after it transfers employees from its two former locations to a historic San Francisco office building in the heart of the business district. Delmore &rsquo; s ofices in Los Angeles will close, as will Lasing &rsquo; s central management headquarters in Del Rio.</strong></p>\r\n<p><strong>Delmore &rsquo; s CEO, Fred Byers, says that customers should not expect any disruption to their service. Starting in March, customers can direct any concerns to Del-Lasing &rsquo; s new online customer service helpdesk.</strong></p>', '9', '19', '2', 'In a business directory ', 'In a newspaper ', 'In a magazine ', 'In a job advertisement ', '<p><strong>Where would someone most likely find this information?</strong></p>', '169', '0', '1', '1', 'vi', '0', '2015-12-28 16:06:17', '0000-00-00 00:00:00');
INSERT INTO `tb_test_item` VALUES ('3377', '<p>Question 153- 154</p>\r\n<p> </p>\r\n<p>Notice</p>\r\n<p>PARKING RATE INCREASE NOTICE</p>\r\n<p>Effective August 01</p>\r\n<p>Due to operational increases and the need for ongoing capital investment in our existing structures, the parking rates at the Montclair Public Parking garage will take effect on August 01.</p>\r\n<p>All existing two- hour meters will be changed to three- hour meters to provide better customer utilization.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"213\">\r\n<p>Address</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>Hours</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>Hourly rate</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"213\">\r\n<p>257 Union Street</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>7 AM- 11 PM</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>*$3.50</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"213\">\r\n<p>1 York Road</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>6 AM- 10 PM</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>$3.50</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"213\">\r\n<p>3800 Bathurst Avenue</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>24 Hours</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>$4.00</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td valign=\"top\" width=\"213\">\r\n<p>70 Vaughn Street</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>6 AM- 2 AM</p>\r\n</td>\r\n<td valign=\"top\" width=\"213\">\r\n<p>*$3.75</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>Saturdays and Sundays- $2 per hour</p>', '9', '20', '3', 'Direction to public parking lots', 'Public service center locations', 'Increased parking price', 'Introduction of new parking meters ', '<p>What is this notice referring to?</p>', '153', '0', '1', '1', 'vi', '0', '2015-12-28 16:07:33', '0000-00-00 00:00:00');
