INSERT INTO `tb_test_item` VALUES ('4058', '<p><strong>Question 155-156</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>WindowWell</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Alman Bros. Installation Co.</strong></p>\r\n<p><strong>16 Pine Street</strong></p>\r\n<p><strong>Framingham 6161</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Dear Friends at Alman Bros. Installation Co.:</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>As the top producer of single and multi-pane windows, WindowWell appreciates your company’s commiment to quality. We enthusiastically recommend our durable and stylish windows for any ongoing, or future construction endeavors.</strong></p>\r\n<p><strong>For this month only, we are offering special discounted purchasing options to new customers. For large orders, we offer up to 40% savings. Please consult our regional WindowWell sales representative for more details. Call now, and we’ll even be including a free estimate for your latest project. Additional contact information is available on our website at </strong><a href=\"http://www.windowwel.com/\"><strong>www.windowwel.com</strong></a><strong>.</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Let WindowWell help you see clearly.</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Sincerely yours,</strong></p>\r\n<p><strong>Jerry Alman</strong></p>\r\n<p><a href=\"http://www.windowwell.com/\"><strong>www.windowwell.com</strong></a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '24', '3', 'To ask for the prices of windows', 'To complain about poor service', 'To promote a special offer', 'To advertise a going-out-of-business sale', '<p><strong>What is the purpose of the letter?</strong></p>', '155', '0', '1', '1', 'vi', '0', '2016-01-05 01:37:25', '0000-00-00 00:00:00');
