INSERT INTO `tb_test_item` VALUES ('4142', '<p><strong>Question 176-180</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Kingstown Rentals</strong></p>\r\n<p><strong>7692 John Ave</strong></p>\r\n<p><strong>Edmonton, Alberta</strong></p>\r\n<p><strong>T5J 1N7</strong></p>\r\n<p><strong>June 10</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Franchesca DeGardines</strong></p>\r\n<p><strong>6556 Archway Place Apt, ioB</strong></p>\r\n<p><strong>Edmonton, Alberta</strong></p>\r\n<p><strong>T5O 1L7</strong></p>\r\n<p><strong>Dear Ms. DeGardines</strong></p>\r\n<p><strong>Thank you for notification of your departure on October 30. As we approach that day, we have some important procedures to discuss.</strong></p>\r\n<p><strong>The building supervisor, Mr. Costeau, will come to your apartment on October 29 to inspect the apartment and ensure that it is in the same basic condition as when you first moved in. If no major damages or unauthorized changes are discovered, the $1,000 security deposit will be mailed to you. In the case of a failed inspection, the anticipated cost of the repairs will be deducted from the deposit. Copies of the repair service billing information can be provided upon request.</strong></p>\r\n<p><strong>When moving out, please refrain from using the main elevators. They have been damaged by furniture before and stall frequently with heavy loads. Please do not use them for any heavy or bulky items. Instead, use the maintenance elevator at the back of the building.</strong></p>\r\n<p><strong>Please meet with a building security staff member to sign out and return the apartment keys on the day of your departure. The security and management office is on the basement floor of the building. Please double-check to make sure that the apartment is clean, and that all items have been removed before leaving. Belongings that are left in the apartment will be treated as abandoned.</strong></p>\r\n<p><strong>If you have any additional questions, feel free to contact the office at 791-549-1864.</strong></p>\r\n<p><strong>Sincerely,</strong></p>\r\n<p><strong><em>Pierre Cardine</em></strong></p>\r\n<p><strong>Property manager</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '24', '2', 'A businesswoman running a real estate firm ', 'A resident of the building', 'An individual who wants to rent a room', 'The owner of the apartment', '<p><strong>Who is Franchesca DeGardines?</strong></p>', '176', '0', '1', '1', 'vi', '0', '2016-01-05 22:56:35', '0000-00-00 00:00:00');
