INSERT INTO `tb_test_item` VALUES ('4569', '<p><strong>Question 169-172</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>JP Store is proud to announce its new catalogue ervie, which will begin this September. Each month all customers with a JP Gold Card will receive a free catalogue. What’s so great about the JP Catalogue? You don’t have to go to our store to buy your clothes. You can simply call the customer service hotline and place your order from the comfort of your own home. Plu, eah month’s catalogue will include special discounts on all our clothing. If you have any questions about this special promotion, contact us at 1-800-2365-9864. If you are not a cardholder and would like to become one, then download an application from our website, </strong><a href=\"http://www.jpclothes.com/\"><strong>www.jpclothes.com</strong></a><strong>. All cutomers who apply online before August 15<sup>th</sup> will receive an additional 20 percent off their first order.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '28', '3', 'Home furnishings', 'Kitchenware', 'Clothing', 'Office supplies', '<p><strong>What kind of product does JP Store sell?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '169', '0', '1', '1', 'vi', '0', '2016-01-09 09:54:18', '0000-00-00 00:00:00');
