INSERT INTO `tb_test_item` VALUES ('4573', '<p><strong>Question 173-175</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Charity Hospital is looking for an organized, motivated individual to fill the position of Director of Dining Services, starting August 15<sup>th</sup>.</strong></p>\r\n<p><strong>Responsibilities of the position include planning weekly meals for patients, managing a staff of thirty food service workers, and placing food supplies orders every month. You may also be asked to organize food for special events, such as hospital benefits.</strong></p>\r\n<p><strong>The successful applicant will have at least five years’ experience working as a manager in some business field, and a university degree. Specific experience in the hospitality industry is preferred, but not required.</strong></p>\r\n<p><strong>Interested individuals should pick up an application from the Food Service Department at Charity Hospital, which is located on the first floor of the hospital. Please fill out the application and submit it, along with 2 reference letters and a cover letter, by July 24, 2007. Applications will be available starting June 21, 2007.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '28', '4', 'Medical assistant', 'Receptionist', 'Hospital director', 'Manager of food services', '<p><strong>What kind of position is being advertised?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '173', '0', '1', '1', 'vi', '0', '2016-01-09 09:56:52', '0000-00-00 00:00:00');
