INSERT INTO `tb_test_item` VALUES ('4782', '<p><strong>Question 153-154</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>From: Hazel Irwin <a href=\"mailto:hirwin@csgmadia.com\">hirwin@csgmadia.com</a></strong></p>\r\n<p><strong>To: Lindsey Monotail <a href=\"mailto:Imonotail@csgmadia.com\">Imonotail@csgmadia.com</a></strong></p>\r\n<p><strong>Subject: Speech Request</strong></p>\r\n<p><strong>Date: May 23</strong></p>\r\n<p><strong> </strong></p>\r\n<p><strong>Hi Lindsey, </strong></p>\r\n<p><strong>As you know, Robert Hulking will be awarded the CSG Excellence in Sales Prize at a special ceremony at the Persian Hotel this Saturday. Mr. Hulking recently informed me that you were his supervisor when he first came to CSG five years ago, and that you and your business advice have greatly contributed to his recent success. To honor both your work as Senior Sales Supervisor and Mr. Hulking’s recent achievement, I would like to ask you to present the award this Saturday. As part of the presentation ceremony, you will be asked to give a brief speech about Mr. Hulking. It would be a good idea to include some personal anecdotes as well as professional stories to help highlight Mr. Hulking’s charater.</strong></p>\r\n<p><strong>Please contact Sara Benhunt, my secretery, before 2 p.m. today and let her know if you can attend the event. Her extension is 3125. I hope you will join us.</strong></p>\r\n<p><strong>Sincerely,</strong></p>\r\n<p><strong>Hazel Irwin</strong></p>\r\n<p><strong>Director of Public Relations</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '29', '3', 'The new sales associate', 'A supervisor in the sales department', 'An employee who will receive an award', 'The Public Relations Director', '<p><strong>Who is Robert Hulking?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '153', '0', '1', '1', 'vi', '0', '2016-01-17 00:03:38', '0000-00-00 00:00:00');
