INSERT INTO `tb_test_item` VALUES ('4784', '<p><strong>Question 155-156</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Do you enjoy working outdoors? Do you thrive in a physically demanding work environment? Then Gateway Contractors has the perfect job for you.</strong></p>\r\n<p><strong>We’re looking for young, energetic individuals to work on Gateway’s latest construction project: the Maplewood Gymnasium. Successful candidates will be responsible for unloading supply trucks and distributing supplies to various key points around the site. No previous construction experience is necessary, but applicants must be able to work well with other people.</strong></p>\r\n<p><strong>Applications are available at <a href=\"http://www.gatewaycontractors.com/\">www.gatewaycontractors.com</a>. To schedule an interview, contact Bruno Morton, the Construction Site Supervisor, at 245-698-7895. Please, no calls after 9 p.m.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '29', '1', 'Construction worker', 'Shipping supervisor', ' Gym instructor', 'Truck driver', '<p><strong>What kind of job is being advertised?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '155', '0', '1', '1', 'vi', '0', '2016-01-17 00:04:59', '0000-00-00 00:00:00');
