INSERT INTO `tb_test_item` VALUES ('4795', '<p><strong>Question 166-167</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Probable Tornadoes devastate Texas Town</strong></p>\r\n<p><strong>Storms Knock out Power To About 20,000 Customers</strong></p>\r\n<p><strong>Panhandle, Texas – Officials in Pahandle issued a duck-to-dawn curfew on Sunday after a group of tornadoes battered the town. The storms destroyed houses and downed power lines, filling the roads with debris. At least 17 people were injured, one is in critical condition. About 50 people from the town of 2,500 are unaccounted for. But country officials said they believe they are all safe. Moore County Judge Stuart Riley said they probably evacuated after hearing tornado sirens. The storms knocked out power to about 20,000 customers. Officials are also checking for gas leaks in the damaged homes. </strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '29', '2', '17', '50', '2,500', '20,000', '<p><strong>How many people are still missing?</strong></p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>\r\n<p> </p>', '166', '0', '1', '1', 'vi', '0', '2016-01-17 00:13:01', '0000-00-00 00:00:00');
