INSERT INTO `tb_test_item` VALUES ('4958', '<p><strong>Question 167-170</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p align=\"center\"><strong>Opening Ceremony for New Research Wing </strong></p>\r\n<p><strong>Countydale Hospital is proud to announce that construction on the new 20-million-dollar research wing has been completed. To celabrate the occasion, the hospital has decided to hold a special ceremony on Saturday, May 14, at 4 p.m. All hospital staff, patients, and city residents are welcome to attend. Below is a basic overview of the schedule of events:</strong></p>\r\n<p><strong>4:00 p.m. – James Rosenthal, Director of Countydale Hospital, will give an introductory speech.</strong></p>\r\n<p><strong>5:00 p.m. – Dr. Merriam Way will discuss the research work that will take place in the new wing.</strong></p>\r\n<p><strong>6:00 p.m – Gary Paulson Jr., President of Wilson Pharmaceuticals, will talk about his 15-million-dollar contribution to the new wing.</strong></p>\r\n<p><strong>7:00 p.m. – The children of City Elementary School will sing a few songs.</strong></p>\r\n<p><strong>7:30 p.m. – Dr. Steven Fellows, Countydale Chief of Surgery, will cut the ribbon and open the doors.</strong></p>\r\n<p><strong>8:00 p.m. – Patients and staff will be invited to enjoy cake and refreshments in the reception area of the new Paulson Research Wing.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '30', '4', 'It was closed temporarily', ' More doctors were hired', ' The number of parients increased', ' The new section was added', '<p><strong>What happened to Countydale Hospital recently?</strong></p>\r\n<p> </p>', '167', '0', '1', '1', 'vi', '0', '2016-01-19 19:49:10', '0000-00-00 00:00:00');
