INSERT INTO `tb_test_item` VALUES ('6023', '<p><strong>Question 155-156</strong></p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td valign=\"top\" width=\"604\">\r\n<p><strong>Claredon Hotels and Resorts – Franchise Development </strong></p>\r\n<p><strong>Clarendon is focused on moving forward. We keep our brand consistent and strong by managing a standard of quality product and service that is second to none, making Clarendon a name that customers can trust. Our expansion strategy covers all of the key areas of support for a successful hotel business. We take our relationships with franchisees seriously and offer lucrative financial packages. </strong></p>\r\n<p><strong>Summary of Fees</strong></p>\r\n<p><strong>INITIAL FEE:  $75,000 (USD)</strong></p>\r\n<p><strong>ROYALTY FEE: 5% of Gross Room Revenue annually </strong></p>\r\n<p><strong>MARKETING CONTRIBUTION: 2% of Gross Room Revenue annually </strong></p>\r\n<p><strong>This applies to the U.S. and Canada only. The information provided here is a summary and does not include all fees and costs. For complete information about fees, services, and licensing requirements, please request a copy of the Clarendon Franchise Information Package from our head officea in Philadelphia.</strong></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>', '9', '31', '4', 'Guests of the hotel', ' Potential employees', ' Upper management in the company', ' Those considering opening a branch ', '<p><strong>Who is this information intended for?</strong></p>\r\n<p> </p>', '155', '0', '1', '1', 'vi', '0', '2016-01-28 16:44:46', '0000-00-00 00:00:00');
