INSERT INTO `tb_test_item` VALUES ('6077', '<p>Question 163- 164</p>\r\n<p> </p>\r\n<p>IMPORTANT NOTICE</p>\r\n<p> </p>\r\n<p>All employees of Roger’s Cable are no longer allowed to park on the side streets on the opposite side of Manordale Avenue. We have received complaints from the residents of the neighborhood and the by law enforcement about employee vehicles. Effective immediately, all staff are required to purchase a parking pass for the underground parking beneath the building for $35 per month. Ask your manager to have the parking fees withheld from your monthly pay deposit. Thank you for your cooperation.</p>', '9', '34', '4', 'The Manordale Resident &rsquo; s Association', 'The employees of Roger &rsquo; s Cable', 'A by- law enforcement officer', 'A manager at the company', '<p>Who issued this notice?</p>', '163', '0', '1', '1', 'vi', '0', '2016-01-28 23:34:47', '0000-00-00 00:00:00');
