INSERT INTO `tb_test_item` VALUES ('6104', '<p>Question 178 -180</p>\r\n<p> </p>\r\n<p>Athena Airlines Passenger Briefing</p>\r\n<p> </p>\r\n<p>This passenger briefing contains information about our flight itinerary to Phoenix and Salt Lake City. Please ask a customer service representative if you need further assistance.</p>\r\n<p> </p>\r\n<p>Thursday 12/23/2007</p>\r\n<p>Depart    SAN DIEGO,CA       BROWN FIELD MUNI                   8:00 am</p>\r\n<p>Arrive     PHOENIX, AZ          PHOENIX SKY HARBOR INTL    9:53 am</p>\r\n<p>               Flight time      00:53  Time change: Add 1 hour(s)</p>\r\n<p>               Catering Info: Light breakfast including Danish, fruit and fresh orange juice</p>\r\n<p> </p>\r\n<p>Thursday 12/23/2007</p>\r\n<p>Depart    PHOENIX,AZ           PHOENIX SKY HARBOR INTL    10:45 am</p>\r\n<p>Arrive     Salt Lake city, UT     SALT LAKE CITY INTL                12:06 pm</p>\r\n<p>               Flight time    01:21    No time change</p>\r\n<p>               Special Notes: Transportation from Salt Lake Airport to your meeting will be provided by K &amp; R Limo. Service- 602- 555- 1234</p>\r\n<p> </p>\r\n<p>Friday 12/24/2007</p>\r\n<p>Depart    PHOENIX CITY,UT SALT LAKE CITY INTL                01:00 pm</p>\r\n<p>Arrive     PHOENIX, AZ          PHOENIX SKY HARBOR INTL    02:15 pm</p>\r\n<p>               Flight time    01:15    No time change</p>\r\n<p>               Catering info: Sandwich tray, fresh fruit and light dessert</p>\r\n<p> </p>\r\n<p>Friday 12/24/2007</p>\r\n<p>Depart    PHOENIX, AZ          PHOENIX SKY HARBOR INTL    03:00 pm</p>\r\n<p>Arrive     SAN DIEGO, CA      BROWN FIELD MUNI                   02:55 pm</p>\r\n<p>          Flight time     00:55       Time Change: Lose 1 hour(s)</p>', '9', '34', '1', 'A traveler &rsquo; s itinerary', 'A train schedule', 'A flight plan', 'A pilot &rsquo; s directions', '<p>What is this information related to?</p>', '178', '0', '1', '1', 'vi', '0', '2016-01-29 01:24:46', '0000-00-00 00:00:0